package com.kh.day04.loop;

public class Exam_ForDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int j = 2; j < 10; j++) {
			for (int i = 1; i < 10; i++) {
				System.out.println(j + " * " + i + " = " + j * i);
			}
			System.out.println();
		}
	}

}
